settings.html:
var last_update = '';
var bgcolor = "green";
var status = "Unknown";
var name = "SVT6Data";
var build = "";
if (datasources[name]["status"] == 'NG' || datasources[name]["status"] == 'WARN' ) {
    status = "Unknown";
    bgcolor = "red";
} else if(datasources[name]["data"]) {
    status = datasources["SVT6Data"]["data"]["status"];
    last_update = datasources[name]["last_update"];
    if (datasources[name]["data"]["build"]) {
        build = datasources[name]["data"]["build"];
    } 
} else {
    status = "Unknown";
    bgcolor = "red";
}
if (status == 'Upgrading') {
    bgcolor = 'GoldenRod';
} else if (status != 'Normal') {
    bgcolor = "red";
}
var last_update = datasources[name]["last_update"];
var body = '<div style="vertical-align: middle;color:white;text-align:center;background-color:'+bgcolor+';font-weight:999;font-size:30px;line-height:100px;height:240px;width:100%;">';
body += "<div style='margin:auto;'>"+status+"</div>";
if (build.length > 0) {
	body += '<div style="color:white;text-align:center;font-weight:999;font-size:20px;line-height:20px;width:100%;">Version: ' + build + '</div>';
}
body += '<div style="color:white;text-align:center;font-weight:999;font-size:14px;line-height:20px;width:100%;">';
body += datasources[name]["last_update"];
body += '</div>'
body += '</div>';
return body;

